private void recursion(int level, int param) {
  // recursion terminator
  if (level > MAX_LEVEL) {

    return;
  }

  // process current layer
  process(level, param);

  // dig into lower layer
  recursion(level: level + 1, newParam);

  // clean current layer

}

private class UnionFind {
    private int[] roots;

    protected UnionFind(int n) {
        this.roots = IntStream.range(0, n).toArray();
    }

    protected int[] getRoots() {
        return this.roots;
    }

    protected void union(int p, int q) {
        roots[this.findRoot(p)] = this.findRoot(q);
    }

    protected void union(int... p) {
        for (int i = 1; i < p.length; i++) {
            this.union(p[i], p[0]);
        }
    }

    protected int findRoot(int i) {
        int root = i;
        while (root != roots[root]) {
            root = roots[root];
        }
        int t;
        while (i != roots[root]) {
            t = roots[i];
            roots[i] = root;
            i = t;
        }
        return root;
    }

    protected boolean isConnected(int p, int q) {
        return this.findRoot(p) == this.findRoot(q);
    }
}

// 计算从起点 start 到终点 target 的最近距离
int BFS(Node start, Node target) {
    Queue<Node> q; // 核心数据结构
    Set<Node> visited; // 避免走回头路

    q.offer(start); // 将起点加入队列
    visited.add(start);
    int step = 0; // 记录扩散的步数

    while (q not empty) {
        int sz = q.size();
        /* 将当前队列中的所有节点向四周扩散 */
        for (int i = 0; i < sz; i++) {
            Node cur = q.poll();
            /* 划重点：这里判断是否到达终点 */
            if (cur is target)
                return step;
            /* 将 cur 的相邻节点加入队列 */
            for (Node x : cur.adj())
                if (x not in visited) {
                    q.offer(x);
                    visited.add(x);
                }
        }
        /* 划重点：更新步数在这里 */
        step++;
    }
}

/*
 * Return true if there is a path from cur to target.
 */
boolean DFS(Node cur, Node target, Set<Node> visited) {
    return true if cur is target;
    for (next : each neighbor of cur) {
        if (next is not in visited) {
            add next to visted;
            return true if DFS(next, target, visited) == true;
        }
    }
    return false;
}

